class UserService:
    def __init__(self, db_connection, cache_client=None):
        self.db = db_connection
        self.cache = cache_client

    def get_user(self, user_id, include_profile=False):
        if self.cache:
            cached = self.cache.get(f"user:{user_id}")
            if cached:
                return cached
        user = self.db.query("SELECT * FROM users WHERE id = ?", user_id)
        if include_profile:
            user['profile'] = self.db.query("SELECT * FROM profiles WHERE user_id = ?", user_id)
        if self.cache:
            self.cache.set(f"user:{user_id}", user, ttl=300)
        return user

    def create_user(self, email, name, role='user'):
        user_id = self.db.insert("users", {"email": email, "name": name, "role": role})
        return {"id": user_id, "email": email, "name": name, "role": role}

def calculate_discount(price, quantity, customer_type):
    if price <= 0 or quantity <= 0:
        raise ValueError("Price and quantity must be positive")

    base_discount = 0
    if quantity >= 100:
        base_discount = 0.15
    elif quantity >= 50:
        base_discount = 0.10
    elif quantity >= 10:
        base_discount = 0.05

    type_discount = {"premium": 0.10, "gold": 0.05, "regular": 0}.get(customer_type, 0)

    total_discount = min(base_discount + type_discount, 0.25)
    return price * quantity * (1 - total_discount)

